import { BizForm, BizFormProps } from 'antd-more';
import { useSetState } from 'rc-hooks';
import { sleep } from 'ut2';
import { Button, Collapse, Divider, Flex, Select, Switch } from 'antd';

const DemoForm: React.FC<BizFormProps> = ({
  labelWidth = 84,
  hideLabel = false,
  layout = 'horizontal',
  requiredMark = true,
  size = 'middle',
  variant = 'outlined',
  children,
  ...restProps
}) => {
  const formName = useId();
  const defaultState = { labelWidth, hideLabel, layout, requiredMark, size, variant };
  const [state, setState] = useSetState({ ...defaultState });

  return (
    <div className="demo-form">
      <Collapse
        bordered={false}
        items={[
          {
            key: '1',
            label: '\u5207\u6362\u663E\u793A/\u9690\u85CF\u914D\u7F6E\u9762\u677F',
            extra: (
              <Button
                type="link"
                size="small"
                onClick={(e) => {
                  e.stopPropagation();
                  setState({ ...defaultState });
                }}
              >
                \u70B9\u51FB\u6062\u590D\u521D\u59CB\u914D\u7F6E
              </Button>
            ),
            children: (
              <Flex gap={16} align="center" wrap>
                <div>
                  layout:&nbsp;
                  <Select
                    options={[
                      { label: 'vertical', value: 'vertical' },
                      { label: 'horizontal', value: 'horizontal' },
                      { label: 'inline', value: 'inline' }
                    ]}
                    value={state.layout}
                    onChange={(value) => {
                      setState({ layout: value });
                    }}
                    style={{ width: 120 }}
                  />
                </div>
                <div>
                  requiredMark:&nbsp;
                  <Select
                    options={[
                      { label: 'true', value: true },
                      { label: 'false', value: false },
                      { label: 'optional', value: 'optional' },
                      { label: 'customize', value: 'customize' }
                    ]}
                    value={state.requiredMark}
                    onChange={(value) => {
                      setState({ requiredMark: value });
                    }}
                    style={{ width: 120 }}
                  />
                </div>
                <div>
                  size:&nbsp;
                  <Select
                    options={[
                      { label: 'small', value: 'small' },
                      { label: 'middle', value: 'middle' },
                      { label: 'large', value: 'large' }
                    ]}
                    value={state.size}
                    onChange={(value) => {
                      setState({ size: value });
                    }}
                    style={{ width: 120 }}
                  />
                </div>
                <div>
                  variant:&nbsp;
                  <Select
                    options={[
                      { label: 'outlined', value: 'outlined' },
                      { label: 'filled', value: 'filled' },
                      { label: 'borderless', value: 'borderless' },
                      { label: 'underlined', value: 'underlined' }
                    ]}
                    value={state.variant}
                    onChange={(value) => {
                      setState({ variant: value });
                    }}
                    style={{ width: 120 }}
                  />
                </div>
                <div>
                  labelWidth:&nbsp;
                  <Select
                    options={[
                      { label: '84', value: 84 },
                      { label: '98', value: 98 },
                      { label: '112', value: 112 },
                      { label: '126', value: 126 },
                      { label: '140', value: 140 },
                      { label: '154', value: 154 },
                      { label: '168', value: 168 },
                      { label: 'auto', value: 'auto' }
                    ]}
                    value={state.labelWidth}
                    onChange={(value) => {
                      setState({ labelWidth: value });
                    }}
                    style={{ width: 120 }}
                  />
                </div>
                <div>
                  hideLabel:&nbsp;
                  <Switch
                    value={state.hideLabel}
                    onChange={(v) => {
                      setState({
                        hideLabel: v
                      });
                    }}
                  />
                </div>
              </Flex>
            )
          }
        ]}
      />
      <Divider />
      <BizForm
        name={formName}
        onFinish={async (values) => {
          await sleep(500);
          console.log(values);
        }}
        scrollToFirstError={{
          behavior(actions) {
            actions.forEach(({ el, top }) => {
              // implement the scroll anyway you want
              el.scrollTop = top - 76;
            });
          }
        }}
        {...state}
        {...restProps}
      >
        {children}
      </BizForm>
    </div>
  );
};

export default DemoForm;
