import { Divider, Radio, type RadioChangeEvent } from 'antd';
import { BizConfigProvider, BizLocale } from 'antd-more';
import zhCN from 'antd-more/es/locale/zh_CN';
import enUS from 'antd-more/es/locale/en_US';
import dayjs from 'dayjs';
import BizTableDemo from '../../biz-table/demos/not-request';
import DrawerFormDemo from '../../biz-form/demos/drawer-form-1';
import ModalFormDemo from '../../biz-form/demos/modal-form-1';
import StepsFormDemo from '../../biz-form/demos/steps-form-1';
import FormDemo from '../../biz-form/demos/Demo1';

dayjs.locale('zh-cn');

function Demo() {
  const [locale, setLocal] = useState<BizLocale>(zhCN);

  const changeLocale = (e: RadioChangeEvent) => {
    const localeValue = e.target.value;
    setLocal(localeValue);
    if (localeValue === zhCN) {
      dayjs.locale('zh-cn');
    } else {
      dayjs.locale('en');
    }
  };

  return (
    <>
      <div>
        <span style={{ marginRight: 16 }}>Change locale of components:</span>
        <Radio.Group value={locale} onChange={changeLocale}>
          <Radio.Button key="en" value={enUS}>
            English
          </Radio.Button>
          <Radio.Button key="cn" value={zhCN}>
            \u4E2D\u6587
          </Radio.Button>
        </Radio.Group>
      </div>
      <BizConfigProvider locale={locale}>
        <h3>\u8868\u5355</h3>
        <FormDemo />
        <Divider />
        <h3>\u6D6E\u5C42\u8868\u5355</h3>
        <DrawerFormDemo />
        <ModalFormDemo />
        <Divider />
        <h3>\u5206\u6B65\u8868\u5355</h3>
        <StepsFormDemo />
        <Divider />
        <h3>\u8868\u683C</h3>
        <BizTableDemo />
      </BizConfigProvider>
    </>
  );
}

export default Demo;
