import { BizInputIcon, BizInputIconData } from 'antd-more';

const Demo = () => {
  const [value, setValue] = React.useState<string>();

  const hasIcon = value && BizInputIconData.has(value);
  const iconView = useMemo(() => {
    if (hasIcon) {
      const IconComp = BizInputIconData.get(value)!;
      return <IconComp />;
    }
    return null;
  }, [hasIcon, value]);

  return (
    <>
      <p>
        \u5F53\u524D\u9009\u62E9\u7684\u56FE\u6807\uFF1A
        {hasIcon ? iconView : '-'}
      </p>
      <BizInputIcon iconData={BizInputIconData} onChange={setValue} column={4} />
    </>
  );
};

export default Demo;
